import "bootstrap/dist/css/bootstrap.css";
import "./App.css";
import { useEffect, useState } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  redirect,
  Navigate,
} from "react-router-dom";
import Profile from "./componenets/Profile";
import HomePage from "./componenets/ProductCards";
import ErrorPage from "./componenets/ErrorPage";
import Login from "./componenets/Login";
import Register from "./componenets/Register";
import ProductCard from "./componenets/ProductCards";
import ProductCards from "./componenets/ProductCards";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Flip } from "react-toastify";
import {
  notifyLoggedIn,
  notifyLoggedOut,
  notifyLoginFailed,
} from "./notification/Login";
import Footer from "./componenets/Footer";
import AboutUs from "./componenets/About";
import LogoutIcon from "./componenets/LogoutIcon";
import CartIcon from "./componenets/CartIcon";
import Cart from "./componenets/Cart";
import { getCartItems } from "./API/CartAPI";
import OrderConfirmation from "./componenets/OrderConfirmation";
import {
  notifyRegistrationFailed,
  notifyRegistrationSuccess,
} from "./notification/Registration";
import MyOrders from "./componenets/MyOrders";
import AddProduct from "./componenets/AddProduct";
import { Base_URL } from "./API/Base_URL";

function App() {
  const [products, setProducts] = useState([]);

  //login
  const [loginEmailOrPhone, setLoginEmailOrPhonesetEmail] = useState("");
  const [loginPwd, setLoginPwd] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userDetail, setUserDetails] = useState({});
  const [loginFailedMsg, setLoginFailedMsg] = useState("");
  const [loginEmailOrPhoneError, setLoginEmailOrPhonesetEmailError] =
    useState("");
  const [loginPwdError, setLoginPwdError] = useState("");

  const [showLoginPwd, setShowLoginPwd] = useState(false);

  //session
  const [sessionItems, setSessionItems] = useState({
    username: "",
    pwd: "",
  });

  //Registration
  const [isRegistrationSuccess, setIsRegistrationSuccess] = useState(false);
  const [gender, setGender] = useState("Male");
  const [dob, setDob] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNo, setPhoneNo] = useState("");
  const [pwd, setPwd] = useState("");
  const [confirmPwd, setConfirmPwd] = useState("");
  const [area, setArea] = useState("");
  const [city, setCity] = useState("");
  const [state1, setState1] = useState("");
  const [country, setCountry] = useState("India");
  const [pincode, setPincode] = useState("");

  //Search
  const [searchProduct, setSearchProduct] = useState("");
  const [pathName, setPathName] = useState("");

  useEffect(() => {
    if (searchProduct.length > 1) {
      setPathName("/");
    } else {
      setPathName(window.location.pathname);
    }
  });

  const onSearchChange = (e) => {
    e.preventDefault();
    setSearchProduct(e.target.value);
    //window.location.href = "/";
  };

  const [filteredProducts, setfilteredProducts] = useState(products);

  useEffect(() => {
    setTimeout(() => {
      let filtered = products.filter((product) =>
        product.title.toLowerCase().includes(searchProduct.toLowerCase())
      );
      setfilteredProducts(filtered);
    }, 500);
  });

  const resetLoginForm = (e) => {
    e.preventDefault();
    setLoginEmailOrPhonesetEmailError("");
    setLoginPwdError("");
    setLoginEmailOrPhonesetEmail("");
    setLoginPwd("");
    setLoginFailedMsg("");
    setShowLoginPwd("");
  };

  const hangleLogin = (e) => {
    e.preventDefault();
    var phoneNumber = 0;
    var email1 = "";
    if (loginEmailOrPhone == "" || loginPwd == "") {
      if (loginEmailOrPhone == "") {
        setLoginEmailOrPhonesetEmailError("Empty username!");
      } else {
        setLoginEmailOrPhonesetEmailError("");
      }
      if (loginPwd == "") {
        setLoginPwdError("Empty password!");
      } else {
        setLoginPwdError("");
      }
    } else {
      setLoginEmailOrPhonesetEmailError("");
      setLoginPwdError("");

      if (loginEmailOrPhone.toString().includes("@")) {
        email1 = loginEmailOrPhone;
      } else {
        phoneNumber = parseInt(loginEmailOrPhone);
      }
      axios
        .post(`${Base_URL}/login`, {
          username: loginEmailOrPhone,
          password: loginPwd,
        })
        .then((response) => {
          if (response.status == 200) {
            setLoginFailedMsg("Login successful, please close this pop-up");
            setIsAuthenticated(true);
            // setUserId(response.data.user.userId);
            sessionStorage.setItem("email", response.data.credentials.email);
            sessionStorage.setItem(
              "phone",
              response.data.credentials.phoneNumber
            );
            sessionStorage.setItem("user_id", response.data.user.userId);
            sessionStorage.setItem("pwd", response.data.credentials.pwd);
            setUserDetails(response.data);
            resetLoginForm(e);
            notifyLoggedIn();
          }
        })
        .catch((error) => {
          notifyLoginFailed();
          if (error.message.includes("401")) {
            setLoginFailedMsg("Incorrect username or password !");
          } else {
            setLoginFailedMsg("Network error !");
          }

          setIsAuthenticated(false);
        });
    }
  };

  const [cartItems, setCartItems] = useState([]);

  const handleLogout = (e) => {
    e.preventDefault();
    sessionStorage.clear();

    setTimeout(() => {
      setIsAuthenticated(false);
      setLoginFailedMsg("");
    }, 500);
    notifyLoggedOut();
  };

  const [pwdMissMatch, setPwdMissmatch] = useState("");
  const [emailError, setEmailError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [firstNameError, setFirstNameError] = useState("");
  const [lastNameError, setLastNameError] = useState("");
  const [dobError, setDobError] = useState("");
  const [pwdError, setPwdError] = useState("");
  const [confirmPwdError, setConfirmPwdError] = useState("");
  const [areaError, setAreaError] = useState("");
  const [cityError, setCityError] = useState("");
  const [state1Error, setState1Error] = useState("");
  const [countryError, setCountryError] = useState("");
  const [pincodeError, setPincodeError] = useState("");

  //cart
  // const [userId, setUserId] = useState();

  const clearRegistrationForm = (e) => {
    e.preventDefault();
    setGender("Male");
    setDobError("");
    setDob("");
    setFirstName("");
    setLastName("");
    setEmail("");
    setPhoneNo("");
    setPwd("");
    setConfirmPwd("");
    setArea("");
    setCity("");
    setState1("Andaman and Nicobar Islands");
    setCountry("India");
    setPincode("");
    setPwdMissmatch("");
    setEmailError("");
    setPhoneError("");
    setFirstNameError("");
    setLastNameError("");
    setPwdError("");
    setConfirmPwdError("");
    setAreaError("");
    setCityError("");
    setState1Error("");
    setCountryError("");
    setPincodeError("");
  };

  const handleRegister = (e) => {
    e.preventDefault();
    if (
      firstName == "" ||
      lastName == "" ||
      email == "" ||
      pwd == "" ||
      confirmPwd == "" ||
      phoneNo == "" ||
      area == "" ||
      city == "" ||
      state1 == "" ||
      country == "" ||
      pincode == "" ||
      dob == ""
    ) {
      if (pwd !== confirmPwd) {
        setPwdMissmatch("Password doesn't match!");
      } else {
        setPwdMissmatch("");
      }

      if (dob == "") {
        setDobError("Please select date of birth!");
      } else {
        setDobError("");
      }

      let pwdRegex =
        "(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}";
      if (!pwd.match(pwdRegex)) {
        setPwdError(
          "Password contains at least 8 characters including at least one upper case letter, one lower case letter,one special symbol(@#$%^&+=) and one number!"
        );
      } else {
        setPwdError("");
      }

      if (confirmPwd == "") {
        setConfirmPwdError("Empty Confirm password!");
      } else {
        setConfirmPwdError("");
      }

      let regexp = "^[A-Za-z]{1,29}$";
      if (!firstName.match(regexp)) {
        setFirstNameError(
          "Please include only alphabets ranging from 1 to 29 characters!"
        );
      } else {
        setFirstNameError("");
      }

      if (!lastName.match(regexp)) {
        setLastNameError(
          "Please include only alphabets ranging from 1 to 29 characters!"
        );
      } else {
        setLastNameError("");
      }

      let emailRegex = "[a-z0-9_%+-]+@[a-z0-9]+.[a-z]{2,3}$";
      if (!email.match(emailRegex)) {
        setEmailError("Please enter valid email!");
      } else {
        setEmailError("");
      }

      let phoneRegex = "^[0-9]{10}$";
      if (!phoneNo.match(phoneRegex)) {
        setPhoneError("Please ensure 10 digit number!");
      } else {
        setPhoneError("");
      }

      if (area == "") {
        setAreaError("Empty area!");
      } else {
        setAreaError("");
      }
      if (city == "") {
        setCityError("Empty city!");
      } else {
        setCityError("");
      }
      if (state1 == "") {
        setState1Error("Empty state!");
      } else {
        setState1Error("");
      }
      if (country == "") {
        setCountryError("Empty country!");
      } else {
        setCountryError("");
      }

      let pincodeRegex = "^[0-9]{6}$";
      if (!pincode.match(pincodeRegex)) {
        setPincodeError("Please ensure 6 digit pincode!");
      } else {
        setPincodeError("");
      }
    } else {
      setFirstNameError("");
      setLastNameError("");
      setPwdError("");
      setConfirmPwdError("");
      setAreaError("");
      setPhoneError("");
      setCityError("");
      setState1Error("");
      setCountryError("");
      setPincodeError("");
      setEmailError("");

      let newUser = {
        user: {
          firstName: firstName,
          lastName: lastName,
          gender: gender,
          dateOfBirth: new Date(dob).toLocaleDateString(),
        },
        credentials: {
          email: email,
          phoneNumber: parseInt(phoneNo + "l"),
          pwd: pwd,
        },
        address: {
          area: area,
          city: city,
          state: state1,
          country: country,
          pinCode: parseInt(pincode + "l"),
        },
      };

      axios
        .post(`${Base_URL}/register`, newUser)
        .then((response) => {
          if (typeof response.data == typeof "") {
            if (response.data.includes("Email and Phone")) {
              setEmailError("Email already exists!");
              setPhoneError("Phone Number already exists!");
            } else {
              setEmailError("");
              setPhoneError("");

              response.data == "Email already exists!"
                ? setEmailError("Email already exists!")
                : setEmailError("");

              response.data == "Phone Number already exists!"
                ? setPhoneError("Phone Number already exists!")
                : setPhoneError("");
            }
          }
          if (typeof response.data == typeof {}) {
            setIsRegistrationSuccess(true);
            setTimeout(() => {
              setIsRegistrationSuccess(false);
            }, 10000);
            notifyRegistrationSuccess();
            clearRegistrationForm(e);
          } else {
            setIsRegistrationSuccess(false);
            notifyLoginFailed();
          }
          setUserDetails(response.data);
        })
        .catch((error) => {
          setIsRegistrationSuccess(false);
          notifyRegistrationFailed();
        });
    }
  };

  const [productsLoadMsg, setProductsLoadMsg] = useState("");

  const handleLogin = () => {
    if (sessionStorage.getItem("pwd") != null) {
      let uname =
        sessionStorage.getItem("email") == ""
          ? sessionStorage.getItem("phone")
          : sessionStorage.getItem("email");
      if (uname != "") {
        let upwd = sessionStorage.getItem("pwd");
        let phone1;
        let email2;
        if (uname.toString().includes("@")) {
          email2 = uname;
        } else {
          phone1 = parseInt(uname);
        }

        axios
          .post(`${Base_URL}/login`, {
            username: uname,
            password: upwd,
          })
          .then((response) => {
            if (response.status == 200) {
              setLoginFailedMsg("Login successful, please close this pop-up");
              setIsAuthenticated(true);
              setUserDetails(response.data);
              redirect("/");
            }
          })
          .catch((error) => {
            if (error.message.includes("401")) {
              setLoginFailedMsg("Incorrect username or password !");
            } else {
              setLoginFailedMsg("Network error !");
            }
            setIsAuthenticated(false);
          });
      }
    } else {
      setIsAuthenticated(false);
    }
  };

  const [healthStatus, setHealthStatus] = useState();

  const getApplicationHealth = () => {
    axios
      .get(`${Base_URL}/actuator/health`)
      .then((response) => {
        setHealthStatus(response.data.status);
        console.log("health:", response.data.status);
      })
      .catch((error) => console.error(error));
  };

  const getProducts = () => {
    axios
      .get(`${Base_URL}/products`)
      .then((response) => {
        setProducts(response.data);
        if (response.data.length === 0) {
          setProductsLoadMsg("Products not available!");
        }
      })
      .catch((error) => {
        setProductsLoadMsg(`Server Error ! : ${error.message}`);
      });
  };
  useEffect(() => {
    handleLogin();
    getCartItems(setCartItems);
    getProducts();
    getApplicationHealth();
  }, []);

  return (
    <Router>
      {products && products.length > 0 ? (
        <div className="">
          <ToastContainer />
          <div className="">
            <div
              class="row border rounded-3 mt-1 p-3"
              style={{ backgroundColor: " #008080" }}
            >
              <Link class=" col-1 fs-4 link-light" to="/">
                <div className="text-start">
                  <img
                    className="bg-light rounded-5"
                    height="30vh"
                    width="30vw"
                    src={require("./componenets/icon3.png")}
                    alt=""
                  ></img>

                </div>
              </Link>
              <span className="col-2 hoverTitle text-light fs-4">&#x2208; - store</span>
              <span className="col-4">
                  <input
                    className="form form-control w-100 border border-info rounded-3"
                    type="search"
                    placeholder="Search for product..."
                    aria-label="Search"
                    value={searchProduct}
                    onChange={(e) => onSearchChange(e)}
                  />
              </span>

              <Link
                  to={`${pathName}`}
                  className="col-1 mt-1 text-decoration-none rounded-3"
                >
                  <img
                    src={require("./Img/search.jpg")}
                    height="20vh"
                    width="20vw"
                    className="rounded-5"
                    alt="icon"
                  ></img>
                </Link>


              {isAuthenticated && (
                <div class="col-1 dropdown mx-3 mt-1">
                  <span
                    style={{ cursor: "pointer" }}
                    className="text-light text-decoration-none"
                  >
                    <div className="text-center">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="16"
                        height="16"
                        fill="currentColor"
                        class="bi bi-person"
                        viewBox="0 0 16 16"
                      >
                        <path d="M8 8a3 3 0 1 0 0-6 3 3 0 0 0 0 6m2-3a2 2 0 1 1-4 0 2 2 0 0 1 4 0m4 8c0 1-1 1-1 1H3s-1 0-1-1 1-4 6-4 6 3 6 4m-1-.004c-.001-.246-.154-.986-.832-1.664C11.516 10.68 10.289 10 8 10s-3.516.68-4.168 1.332c-.678.678-.83 1.418-.832 1.664z" />
                      </svg>
                    </div>
                    <span className="hoverLink text-light">Account</span>
                    {/* <span className="fs-5 text-light mx-1">&#8964;</span> */}
                  </span>
                  <div class="dropdown-content p-4 rounded-4">
                    {userDetail && (
                      <div>
                        <div className="my-2">
                          {userDetail.user.firstName +
                            " " +
                            userDetail.user.lastName}
                        </div>
                        <div className="my-2">
                          {userDetail.credentials.email}
                        </div>
                        <div className="my-2">
                          {userDetail.credentials.phoneNumber}
                        </div>
                      </div>
                    )}
                    <Link to="/profile" className="btn link-primary rounded-3">
                      Profile &#x27A3;
                    </Link>

                    <Link to="/orders" className="btn link-primary rounded-3">
                      My Orders &#x27A3;
                    </Link>
                  </div>
                </div>
              )}

              {!isAuthenticated && (
                <div className="col-4 text-end">
                  <Link
                  to="/login"
                  type="button"
                  class="mx-2 btn btn-outline-light"
                >
                  Login
                </Link>
                <Link
                  to="/register"
                  className="btn btn-outline-light"
                >
                  Sign Up
                </Link>
                  </div>
              )}

             {
              isAuthenticated && 
              <div>
                 <span class="nav-item col-3">
                <a className="nav-link">
                  <div className="">
                    <div className="">
                      {isAuthenticated && (
                        <div className="col-1 mx-3">
                          <Link
                            to="/cart"
                            className="rounded-4"
                            data-toggle="tooltip"
                            data-placement="top"
                            title="cart"
                          >
                            <span className="fs-6">
                              <a class="" style={{ position: "relative" }}>
                                <span class="badge badge-light text-info">
                                  &#x1F6D2;
                                </span>
                                <i
                                  class="text-warning"
                                  style={{
                                    position: "absolute",
                                    top: "-10px",
                                    right: "-5px",
                                  }}
                                >
                                  {cartItems != undefined &&
                                  cartItems.length != 0
                                    ? cartItems.length
                                    : ""}
                                </i>
                              </a>
                            </span>
                          </Link>
                          <div>
                            <Link
                              to="/cart"
                              className="rounded-4 text-decoration-none"
                            >
                              <span className="cartIcon text-light">Cart</span>
                            </Link>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </a>
              </span>

              <span className="col-2">
                {isAuthenticated && (
                  <div className="mx-4 mt-1">
                    <span
                      className="text-decoration-none text-light"
                      data-toggle="modal"
                      data-target="#logout"
                      style={{ cursor: "pointer" }}
                    >
                      <div className="text-center">
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          width="16"
                          height="16"
                          fill="currentColor"
                          class="bi bi-box-arrow-right"
                          viewBox="0 0 16 16"
                        >
                          <path
                            fill-rule="evenodd"
                            d="M10 12.5a.5.5 0 0 1-.5.5h-8a.5.5 0 0 1-.5-.5v-9a.5.5 0 0 1 .5-.5h8a.5.5 0 0 1 .5.5v2a.5.5 0 0 0 1 0v-2A1.5 1.5 0 0 0 9.5 2h-8A1.5 1.5 0 0 0 0 3.5v9A1.5 1.5 0 0 0 1.5 14h8a1.5 1.5 0 0 0 1.5-1.5v-2a.5.5 0 0 0-1 0z"
                          />
                          <path
                            fill-rule="evenodd"
                            d="M15.854 8.354a.5.5 0 0 0 0-.708l-3-3a.5.5 0 0 0-.708.708L14.293 7.5H5.5a.5.5 0 0 0 0 1h8.793l-2.147 2.146a.5.5 0 0 0 .708.708z"
                          />
                        </svg>
                      </div>

                      <span className="hoverLink">Log-out</span>
                    </span>
                    <div
                      class="modal fade"
                      id="logout"
                      tabindex="-1"
                      role="dialog"
                      aria-labelledby="logout"
                      aria-hidden="true"
                    >
                      <div class="modal-dialog" role="document">
                        <div class="modal-content">
                          <div class="modal-header">
                            <h5 class="" id="logout">
                              Do you want to logout ?
                            </h5>
                          </div>

                          <div class="modal-footer text-center">
                            <button
                              type="button"
                              class="btn btn-primary"
                              data-dismiss="modal"
                            >
                              No
                            </button>
                            <button
                              type="button"
                              class="btn btn-danger"
                              onClick={(e) => handleLogout(e)}
                              data-dismiss="modal"
                            >
                              Yes
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </span>

              <span class="col-2 nav-item active mt-4">
                <p class="nav-link text-light rounded-2 p-2">
                  {isAuthenticated && (
                    <span className="">
                      Welcome, {userDetail.user.firstName} &#128522;
                    </span>
                  )}
                </p>
              </span>
                </div>
             }
            </div>
          </div>

          {isAuthenticated && userDetail.user.role === "ADMIN" && (
            <div
              className={`text-center mt-2 ${
                healthStatus === "UP" ? "text-success" : "text-danger"
              }`}
            >
              Application Health Status : {healthStatus}
            </div>
          )}

          <Routes>
            <Route
              path="/"
              element={
                <ProductCards
                  products={filteredProducts}
                  searchInput={searchProduct}
                  userDetail={userDetail}
                  setCartItems={setCartItems}
                  isAuthenticated={isAuthenticated}
                  pathName={pathName}
                  searchProduct={searchProduct}
                  onSearchChange={onSearchChange}
                />
              }
            />
            <Route
              path="/profile"
              element={
                isAuthenticated ? (
                  <Profile userDetail={userDetail} handleLogin={handleLogin} />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route
              path="/*"
              element={
                <ErrorPage
                  filteredProducts={filteredProducts}
                  msg={productsLoadMsg}
                />
              }
            />
            <Route
              path="/about-us"
              element={<AboutUs msg={productsLoadMsg} />}
            />
            <Route
              path="/cart"
              element={
                !isAuthenticated ? (
                  <Navigate to="/" />
                ) : (
                  <Cart
                    isAuthenticated={isAuthenticated}
                    userDetails={userDetail}
                    setCartItems1={setCartItems}
                    handleLogin={handleLogin}
                  />
                )
              }
            />
            <Route path="/order-confirmation" element={<OrderConfirmation />} />
            <Route
              path="/login"
              element={
                isAuthenticated ? (
                  <Navigate to="/" />
                ) : (
                  <Login
                    hangleLogin={hangleLogin}
                    loginEmailOrPhoneError={loginEmailOrPhoneError}
                    loginEmailOrPhone={loginEmailOrPhone}
                    setLoginEmailOrPhonesetEmail={setLoginEmailOrPhonesetEmail}
                    loginPwdError={loginPwdError}
                    showLoginPwd={showLoginPwd}
                    loginPwd={loginPwd}
                    setLoginPwd={setLoginPwd}
                    setShowLoginPwd={setShowLoginPwd}
                    isAuthenticated={isAuthenticated}
                    loginFailedMsg={loginFailedMsg}
                    resetLoginForm={resetLoginForm}
                  />
                )
              }
            />
            <Route
              path="/register"
              element={
                <Register
                  handleRegister={handleRegister}
                  gender={gender}
                  setGender={setGender}
                  firstNameError={firstNameError}
                  firstName={firstName}
                  setFirstName={setFirstName}
                  lastNameError={lastNameError}
                  lastName={lastName}
                  setLastName={setLastName}
                  dob={dob}
                  setDob={setDob}
                  emailError={emailError}
                  email={email}
                  setEmail={setEmail}
                  phoneError={phoneError}
                  phoneNo={phoneNo}
                  setPhoneNo={setPhoneNo}
                  pwdError={pwdError}
                  pwd={pwd}
                  setPwd={setPwd}
                  confirmPwdError={confirmPwdError}
                  pwdMissMatch={pwdMissMatch}
                  confirmPwd={confirmPwd}
                  setConfirmPwd={setConfirmPwd}
                  areaError={areaError}
                  area={area}
                  setArea={setArea}
                  cityError={cityError}
                  city={city}
                  setCity={setCity}
                  state1Error={state1Error}
                  state1={state1}
                  setState1={setState1}
                  countryError={countryError}
                  country={country}
                  setCountry={setCountry}
                  pincodeError={pincodeError}
                  pincode={pincode}
                  setPincode={setPincode}
                  isRegistrationSuccess={isRegistrationSuccess}
                  clearRegistrationForm={clearRegistrationForm}
                  dobError={dobError}
                />
              }
            />
            <Route
              path="/orders"
              element={
                !isAuthenticated ? (
                  <Navigate to="/" />
                ) : (
                  <MyOrders userId={sessionStorage.getItem("user_id")} />
                )
              }
            />
          </Routes>
          <Footer />
        </div>
      ) : (
        <ErrorPage />
      )}
    </Router>
  );
}

export default App;
