import React from "react";
import "../App.css";

export default function SearchBar({ searchProduct, onSearchChange }) {
  return (
    <form
      class="row form my-2 mx-1  form-inline my-lg-0"
      onSubmit={handleSearch}
    >
      <div class="col-9 dropdown">
        <input
          class="dropdown-toggle border border-info search-input p-2"
          type="search"
          id="dropdownMenuButton"
          data-toggle="dropdown"
          aria-haspopup="true"
          placeholder="Search for product..."
          aria-label="Search"
          value={searchProduct}
          onChange={(e) => onSearchChange(e)}
          style={{
            borderRadius: "40px 0px 0px 40px",
            borderRight: "0px solid white",
          }}
        />
        <div
          class="dropdown-menu px-3 bg-secondary"
          aria-labelledby="dropdownMenuButton"
        >
          {searchProduct.length !== 0 && filteredProducts.length === 0 && (
            <div className="text-center text-warning px-5">No match found!</div>
          )}
          {searchProduct.length !== 0 ? (
            filteredProducts.map((product) => (
              <div className="px-5 my-2">
                <Link
                  className="text-decoration-none text-light"
                  data-bs-toggle="modal"
                  data-bs-target={`#${product.productId}modal`}
                >
                  {product.title}
                </Link>
                {filteredProducts.length !== 1 && <hr />}
              </div>
            ))
          ) : (
            <></>
          )}
        </div>
      </div>
      <Link className="col-2 form-inline" to="/">
        <button
          class="p-2 btn btn-light border border-info"
          type="submit"
          className="p-2 border border-info search-btn"
          style={{
            borderRadius: "0px 40px 40px 0px",
            borderLeft: "0px solid white",
          }}
        >
          <img
            src={require("../Img/search.jpg")}
            height="20vh"
            width="20vw"
            className="rounded-5"
            alt="icon"
          ></img>
        </button>
      </Link>
    </form>
  );
}
